import axios from 'axios';
import CategoryTable from './components/CategoryTable.vue';
import CategoryForm from './components/CategoryForm.vue';
export default {
  name: 'App',
  components: {
    CategoryTable,
    CategoryForm
  },
  data() {
    return {
      categories: []
    };
  },
  mounted() {
    this.fetchCategories();
  },
  methods: {
    async fetchCategories() {
      try {
        const response = await axios.get('http://localhost:3000/categories');
        this.categories = response.data.categories;
        console.log('Fetched categories:', this.categories);
      } catch (error) {
        console.error('Error fetching categories:', error);
        console.error('Error response:', error.response); // Log the response for more details
      }
    },
    async deleteCategory(id) {
      try {
        await axios.delete(`http://localhost:3000/categories/${id}`);
        this.fetchCategories();
        console.log(`Category with ID ${id} deleted successfully.`);
      } catch (error) {
        console.error('Error deleting category:', error);
        console.error('Error response:', error.response); // Log the response for more details
      }
    },
    async createCategory(category) {
      try {
        await axios.post('http://localhost:3000/categories', category);
        this.fetchCategories();
        console.log('Category created successfully:', category);
      } catch (error) {
        console.error('Error creating category:', error);
        console.error('Error response:', error.response); // Log the response for more details
      }
    },
    async updateCategory(category) {
      try {
        await axios.put(`http://localhost:3000/categories/${category.id}`, category);
        this.fetchCategories();
        console.log(`Category with ID ${category.id} updated successfully.`);
      } catch (error) {
        console.error('Error updating category:', error);
        console.error('Error response:', error.response); // Log the response for more details
      }
    },
    // New method for adding category
    async addCategory(category) {
      try {
        await axios.post('http://localhost:3000/categories', category);
        this.fetchCategories();
        console.log('Category added successfully:', category);
      } catch (error) {
        console.error('Error adding category:', error);
        console.error('Error response:', error.response); // Log the response for more details
      }
    }
  }
};
